import React, { Component } from "react";
import '../../App.css';
import LeftDrawer from '../Drawer/Drawer';
import * as searchbarStyles from  "../../styles/threatimages";
import axios from 'axios';
import { connect } from "react-redux";
import * as inputField from  "../../styles/inputField";
import * as threatDisplayStyles from "../../styles/threatimages";
import Button from '@material-ui/core/Button'
import {searchButton} from "../../styles/button";
import { Field, reduxForm } from "redux-form";
import { Link, Redirect } from "react-router-dom";


class SearchThreats extends Component {

    constructor(props){
        super(props); 
        this.props.threats();
        this.state ={
            redirectVar :null
        }     
    }
    componentDidMount(){
     
        
    }

    renderField(field) {
     
        return (
            <div>
                <input style= {field.style} required placeholder={field.placeholder} type={field.type} {...field.input} />
            </div>  
            
        );
    }
    render() {
       console.log(this.props.threatlinks.threat1);


        return (
        <div   >
         
           
            <LeftDrawer></LeftDrawer>
            <div>
                 <form>
                <table>
                <tr>
               <td>
                 <Field placeholder="Search by date, city, location, severity or certainty of threat"
                                    name="searchquery"
                                    style= {inputField.searchInput}
                                    type = "text"
                                    component={this.renderField}
                                />
                </td>
                <td>
                <Button  style={searchButton} type="submit"  variant="contained" onClick={console.log("Search Button Clicked")}>Search</Button>
                </td>
                </tr>
                </table>
                </form>
             </div>

             <div>
                {this.state.redirectVar}
                 <table style={threatDisplayStyles.threatImageTable}>
                     <tr>
                        <td>
                              <img src={this.props.threatlinks.threat1} style={threatDisplayStyles.threatImage} onClick={()=>{this.props.threatsummary(1) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                            <img src={this.props.threatlinks.threat2}style={threatDisplayStyles.threatImage}  onClick={()=>{this.props.threatsummary(2) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                            <img src={this.props.threatlinks.threat3} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(3) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                            <img src={this.props.threatlinks.threat4} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(4) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                     </tr>
                     <tr>
                        <td>
                          <img src={this.props.threatlinks.threat5} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(5) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                            <img src={this.props.threatlinks.threat6} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(6) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                         <img src={this.props.threatlinks.threat7} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(7) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                        <td>
                            <img src={this.props.threatlinks.threat8} style={threatDisplayStyles.threatImage}onClick={()=>{this.props.threatsummary(8) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>
                        </td>
                     </tr>
                 </table>
             </div>
   
        </div> 

        );
    }
}


const mapStateToProps = state =>{
   
    return {
        threatlinks : state.threatsReducer.threatlinks
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        threats : () => {
            axios.get('http://localhost:3001/all-threats').then(response=>{
                console.log(response.data);
                dispatch({type: "threats",payload : response.data})
            }) 
        },

        threatsummary : (id) => {
            
                dispatch({type: "threatsummary",payload : id})
            }
        }
                    
            
        
    
}

export default reduxForm({
    // validate,
     form: "SignupForm",
 })(connect(mapStateToProps,mapDispatchStateToProps)(SearchThreats));
