import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import '../../App.css';

import Button from '@material-ui/core/Button'
import {signupForm} from "../../styles/signupForm";
import {container} from "../../styles/container";
import * as inputField from  "../../styles/inputField";
import {button} from "../../styles/button";

class Email extends Component {

    constructor(props) {
        super(props);
        this.state = { subject: 'Threat Detection Alert', content:'Threat Detected', name: 'Aysha Godil', email: 'ayshagodil@gmail.com' };
   
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    

    render() {
        return (
           
              <input type="button" value="Submit"  onClick={this.handleSubmit} />
          
          )
    }
    handleChange(event) {
    this.setState({feedback: event.target.value})
  }
  handleSubmit (event) {
 
	const templateId = 'email';

this.sendFeedback(templateId,{subject: this.state.subject, content:this.state.content,email: this.state.email, from_name: this.state.name, reply_to: this.state.email})
  }

  sendFeedback (templateId, variables) {
	window.emailjs.send(
  	'gmail', templateId,
  	variables
  	)
  	.catch(error => console.error('Error found:', error))
  }
}



const mapStateToProps = state =>{
   
    return {
        authFlag : state.signupReducer.authFlag,
        username : state.signupReducer.username,
        password : state.signupReducer.password
      //  login_status : state.reducer.login_status,
       // type : state.reducer.type  
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        signup : (values) => {
                    dispatch({type: "signup",payload : values})
            
        }
    }
}
export default Email;
