import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import '../../App.css';
//import axios from 'axios';
//import {Redirect} from 'react-router';
import Button from '@material-ui/core/Button'
import { makeStyles } from '@material-ui/core/styles';


class SignUp extends Component {

    constructor(props){
        super(props);       
        this.onSubmit = this.onSubmit.bind(this);
    }
 
    renderField(field) {
        const { meta: { touched, error } } = field;
        const className = `form-group ${touched && error ? "has-danger" : ""}`;       

        return (
            <div className={className}>
                <input className="form-control" placeholder={field.placeholder} type={field.type} {...field.input} />
                <div className="text-help">
                    {touched ? error : ""}
                </div>
            </div>
        );
    }

  
 
   componentDidMount()
   {
     //set the with credentials to true
     //axios.defaults.withCredentials = true;
   }
    onSubmit = (values) => {
    
      // this.props.login(values, () => {
       // this.props.history.push("/login");
      //});
       
       
    }

    render() {
        const classes = styles;
       // const { handleSubmit } = this.props;

      //  let redirectVar = null;
      //  let notvalidatedalert = null;
       
        //if(this.props.authFlag)
        //{            
        // if (cookie.load('cookie')) {
         //    console.log("Redirecting to Home");
         //    redirectVar = <Redirect to="/home" />
        // }
         //else if (this.props.authFlag === false && (this.props.login_status === "Invalid Information"  || this.props.login_status === "Username does not exist"))
         //{
          //  notvalidatedalert = <h6>Incorrect username or password entered. Please try again</h6>
       // }
        return (

            <div>
                 <form className={classes.form} onSubmit={console.log('SignUp')}>
                        <h3>SignUp</h3>
                                <Field
                                    placeholder="First Name"
                                    name="firstname"
                                    type = "text"
                                    component={this.renderField}
                                />
                               <Field
                                    placeholder="Last Name"
                                    name="lastname"
                                    type="text"
                                    component={this.renderField}
                                />
                                <Field
                                    placeholder="Username"
                                    name="username"
                                    type = "username"
                                    component={this.renderField}
                                />
                                <Field
                                    placeholder="Password"
                                    name="password"
                                    type = "password"
                                    component={this.renderField}
                                />
                                <Field
                                    placeholder="Confirm Password"
                                    name="confirm"
                                    type = "password"
                                    component={this.renderField}
                                />
                                <Button type="submit" fullWidth color="primary" variant="contained">Sign Up</Button>
                            </form>

                        </div>

        );
    }
}

const styles = makeStyles(theme => ({
    form: { 
        marginTop: theme.spacing(3)
    }  
  }))

const mapStateToProps = state =>{
   
    return {
        authFlag : state.signupReducer.authFlag,
       // username : state.reducer.username,
      //  login_status : state.reducer.login_status,
       // type : state.reducer.type  
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        signup : (values) => {
                    dispatch({type: "signup",payload : values})
            
        }
    }
}
export default reduxForm({
   // validate,
    form: "SignupForm",
    reducer: "signupReducer"
})(connect(mapStateToProps,mapDispatchStateToProps)(SignUp));
