import React, { Component } from "react";
import '../../App.css';
import LeftDrawer from '../Drawer/Drawer';
import * as searchbarStyles from  "../../styles/threatimages";
import axios from 'axios';
import { connect } from "react-redux";
import * as inputField from  "../../styles/inputField";
import * as threatDisplayStyles from "../../styles/threatimages";
import Button from '@material-ui/core/Button'
import {searchButton} from "../../styles/button";
import { Field, reduxForm } from "redux-form";
import { Link, Redirect } from "react-router-dom";


class SearchThreats extends Component {

    constructor(props){
        super(props); 
              
    }
    componentDidMount(){
        this.props.summary();
    }

    renderField(field) {
     
        return (
            <div>
                <input style= {field.style} required placeholder={field.placeholder} type={field.type} {...field.input} />
            </div>  
            
        );
    }
    render() {
       


        return (
        <div   >
         
           
            <LeftDrawer></LeftDrawer>
            <div>
                 <form>
                <table>
                <tr>
               <td>
                 <Field placeholder="Search by date, city, location, severity or certainty of threat"
                                    name="searchquery"
                                    style= {inputField.searchInput}
                                    type = "text"
                                    component={this.renderField}
                                />
                </td>
                <td>
                <Button  style={searchButton} type="submit"  variant="contained" onClick={console.log("Search Button Clicked")}>Search</Button>
                </td>
                </tr>
                </table>
                </form>
             </div>

             <div>
                 <table style={threatDisplayStyles.threatImageTable}>
                     <tr>
                        <td>
                              <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-1.png" style={threatDisplayStyles.threatImage} onclick={()=>console.log('Threat Clicked')}></img>
                        </td>
                        <td>
                            <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-6.png"style={threatDisplayStyles.threatImage} ></img>
                        </td>
                        <td>
                            <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-3.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                        <td>
                            <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-4.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                     </tr>
                     <tr>
                        <td>
                          <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-5.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                        <td>
                            <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-6.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                        <td>
                         <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-7.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                        <td>
                            <img src="https://295-videos.s3.us-east-2.amazonaws.com/images/icon-1.png" style={threatDisplayStyles.threatImage}></img>
                        </td>
                     </tr>
                 </table>
             </div>
   
        </div> 

        );
    }
}


const mapStateToProps = state =>{
   
    return {
      
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        summary : () => {
            axios.get('http://localhost:3001/search-threats').then(response=>{
                dispatch({type: "summary",payload : response.data})
            })
                    
            
        }
    }
}

export default reduxForm({
    // validate,
     form: "SignupForm",
 })(connect(mapStateToProps,mapDispatchStateToProps)(SearchThreats));
