import React, { Component } from "react";
import '../../App.css';
import LeftDrawer from '../Drawer/Drawer';
import axios from 'axios';
import { connect } from "react-redux";
import * as inputField from  "../../styles/inputField";
import * as threatDisplayStyles from "../../styles/threatimages";
import Button from '@material-ui/core/Button'
import {searchButton} from "../../styles/button";
import * as radioStyles from "../../styles/searchRadio";
import { Field, reduxForm } from "redux-form";
import {  Redirect } from "react-router-dom";
import * as threatDetails from "./Threats";

import io from "socket.io-client";
import backendURL from "../../backendUrl";

class SearchThreats extends Component {

    constructor(props){
        super(props); 
       if(this.props.threatlinks.length == 0)
             this.props.threats();
       this.state ={
        redirectVar :null,
    } 
         this.search = this.search.bind(this);
    }
    componentDidMount(){

          var socket = io(backendURL);
          socket.on('update', function (update)
          {
                console.log('recvd ' + update.link );
          });
    }
    
    renderField(field) {
     
        return (
            <div>
                <input style= {field.style} required placeholder={field.placeholder} type={field.type} {...field.input} />
            </div>  
            
        );
    }
    search = (e) =>{
        e.preventDefault();
        console.log('Search attempted');
        console.log(e.target);
    }
    render() {
  
    var threatsDisplay=[] ;
    /*var threatsDisplay =  this.props.threatlinks.map((threat) => (
                
            <td>
              <img src ={threat} style={threatDisplayStyles.threatImage}></img>
            </td> 
         
         )) ;*/
         console.log(threatDetails.threatDetails.threats);
         for(var i = 0; i<this.props.threatlinks.length; i++)
            {
                 const index =i;
                threatsDisplay.push(<td></td>);
                threatsDisplay.push(<img src ={this.props.threatlinks[index].link} style={threatDisplayStyles.threatImage} onClick={()=>{this.props.threatsummary(index) ; this.setState({redirectVar : <Redirect to="/threat-summary"/>})}}></img>);
                threatsDisplay.push(<div style={threatDisplayStyles.threatDetails}>
                    <table>
                    <tr>
                        <td><b>Date</b></td>
                        <td style={threatDisplayStyles.threatDetailsPadding}>{this.props.threatlinks[index].date/*threatDetails.threatDetails.threats[i].date*/}</td>
                    </tr>
                    <tr>
                        <td><b>Location</b></td>
                        <td style={threatDisplayStyles.threatDetailsPadding}>{threatDetails.threatDetails.threats[i].location}</td>
                    </tr>
                    <tr>
                        <td><b>City</b></td>
                        <td style={threatDisplayStyles.threatDetailsPadding}>{threatDetails.threatDetails.threats[i].city}</td>
                    </tr>
                    <tr>
                        <td><b>Severity</b></td>
                        <td style={threatDisplayStyles.threatDetailsPadding}>{this.props.threatlinks[index].severity}</td>
                    </tr>
                    <tr>
                        <td><b>Certainty</b></td>
                        <td style={threatDisplayStyles.threatDetailsPadding}>{this.props.threatlinks[index].certainty}</td>
                    </tr>
                </table>
                </div>)
                if(i+1<this.props.threatlinks.length && (i+1)%4 ==0)
                    threatsDisplay.push(<tr></tr>);
            }
    
        return (
        <div   >
            <LeftDrawer></LeftDrawer>
            <div>
                <form>
                    <table>
                        <tr>
                            <td>
                                <Field placeholder="Search by date, city, location, severity or certainty of threat"
                                       name="searchquery"
                                       style= {inputField.searchInput}
                                       type = "text"
                                       component={this.renderField}
                                />
                            </td>
                            <td>
                                <Button  style={searchButton} type="submit"  variant="contained" onClick={this.search}>Search</Button>
                            </td>
                        </tr>
                        <tr >
                                <label style={radioStyles.radioLabel} ><input type="radio" name="searchRadio" id="dateRadio"  style={radioStyles.searchRadio}></input>Date</label>
                                <label style={radioStyles.radioLabel} ><input type="radio" name="searchRadio" id="cityRadio"  style={radioStyles.radioMargin}></input>City</label>
                                <label style={radioStyles.radioLabel} ><input type="radio" name="searchRadio" id="locationRadio"  style={radioStyles.radioMargin}></input>Location</label>
                                <label style={radioStyles.radioLabel} ><input type="radio" name="searchRadio" id="severityRadio"  style={radioStyles.radioMargin}></input>Severity</label> 
                                <label style={radioStyles.radioLabel}><input type="radio" name="searchRadio" id="certaintyRadio" style={radioStyles.radioMargin}></input>Certainty</label>  
                        </tr>
                    </table>
                </form>
            </div>

            <div>
                {this.state.redirectVar}
                 <table style={threatDisplayStyles.threatImageTable} >
                    {threatsDisplay}
                 </table>
            </div>
   
        </div> 

        );
    }
}


const mapStateToProps = state =>{
   
    return {
        threatlinks : state.threatsReducer.threatlinks,
        el : state.threatsReducer.el
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        threats : () => {
            axios.get(backendURL + '/all-threats').then(response=>{
                console.log(response.data);
              // const threatIds = response.data;
              //  axios.post(backendURL + '/threat-details', response.data).then(result=>{
              //          console.log(result);
               // })
                dispatch({type: "threats",payload : response.data})
            }) 
        },

        threatsummary : (id) => {
            
                dispatch({type: "threatsummary",payload : id})
            },
        
                    
        updateel : () => {
            
            dispatch({type: "updateel",payload : 0})
        }
    
    }
        
    
}

export default reduxForm({
    // validate,
     form: "SignupForm",
 })(connect(mapStateToProps,mapDispatchStateToProps)(SearchThreats));
