import React, { Component } from "react";
import '../../App.css';
import LeftDrawer from '../Drawer/Drawer';
import * as imageStyles from  "../../styles/image";
import * as videoStyles from  "../../styles/video";
import axios from 'axios';
import { connect } from "react-redux";
import * as threats from "./Threats";


class ThreatSummary extends Component {

    constructor(props){
        super(props); 
              
    }
    componentDidMount(){
      //  this.props.summary();
    }
    render() {
        var threatDetails = {};
        switch(this.props.id)
        {
            case 1: threatDetails = threats.threat1; break
            case 2: threatDetails = threats.threat2; break
            case 3: threatDetails = threats.threat3; break
            case 4: threatDetails = threats.threat4; break
            case 5: threatDetails = threats.threat5; break
            case 6: threatDetails = threats.threat6; break
            case 7: threatDetails = threats.threat7; break
            case 8: threatDetails = threats.threat8; break


        }

        var date = (new Date().getMonth() + 1) + "/" + new Date().getDate() + "/" + new Date().getFullYear();
        var location = "37.3352째 N, 121.8811째 W";
        var city = "San Jose"
        var camera = "A103";
        var certainty = "93%";
        var severity = "High";


        return (
        <div  style={videoStyles.background} >
         
           
            <div  style={videoStyles.videoContainer}  >
            <LeftDrawer></LeftDrawer>
            <div style={videoStyles.videoFrame}>
                <img style={videoStyles.video} src={''+this.props.link} width='100%;' height='500px'></img>
            </div>
              {/*   <iframe style={videoStyles.video} width="700px" height="400px" src="https://295-videos.s3.us-east-2.amazonaws.com/San-Jose-16th-Oct-2019.mp4"></iframe> */}
                <div style = {videoStyles.videoInfo}><h5> Date: {threatDetails.date} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Location: {threatDetails.location} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; City: {threatDetails.city}  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  Camera: {threatDetails.camera} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  Severity: {threatDetails.severity}  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  Certainty: {threatDetails.certainty}</h5></div>
              
            </div>
   
        </div> 

        );
    }
}


const mapStateToProps = state =>{
   
    return {
        link : state.threatsReducer.link,
        id : state.threatsReducer.id
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        summary : () => {
            axios.get('http://localhost:3001/threat-summary').then(response=>{
                dispatch({type: "summary",payload : response.data})
            })
                    
            
        }
    }
}

export default (connect(mapStateToProps,mapDispatchStateToProps)(ThreatSummary));
