import React, { Component } from "react";
import '../../App.css';
import LeftDrawer from '../Drawer/Drawer';
import * as videoStyles from  "../../styles/video";
import axios from 'axios';
import { connect } from "react-redux";
import { SignalingClient } from 'amazon-kinesis-video-streams-webrtc';
import backendURL from '../../backendUrl';
import { Redirect } from "react-router-dom";
import ReactHLS from 'react-hls';
import Hls from "hls.js";
import $ from "jquery";
import AWS from "aws-sdk";
import Button from '@material-ui/core/Button';
import DateTimePicker from 'react-datetime-picker';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

import TimePicker from 'rc-time-picker';
import 'rc-time-picker/assets/index.css';

class SurveillanceHistory extends Component {

    constructor(props){
        super(props); 
        this.state = {
            startDate : new Date()
        }
        if(this.props.allThreats.length == 0)
             this.props.threats();
        
    }
    componentDidMount(){
        this.props.surveillance();
       
       // console.log("user : "  + sessionStorage.getItem("user"))
       // if(sessionStorage.getItem("user") == null)
         //   this.setState({redirectVar : <Redirect to= "/signin"/>})
       
    }
    componentWillMount (){
       
    }

    
    handleChange = date => 
    {
        this.setState({ startDate : date })
    }
    render() {
        var date = (new Date().getMonth() + 1) + "/" + new Date().getDate() + "/" + new Date().getFullYear();
        var location = "37.3352° N, 121.8811° W";
        var city = "San Jose"
        var camera = "A103";
       
        var protocol = 'HLS';
        var streamName = 'threatStream';
        var playbackMode = 'ON_DEMAND';
        var playerName = 'HLS.js';

        var options = {
            accessKeyId: 'AKIAXBXQVKC4TY2ZUR6V',
            secretAccessKey: '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',
            sessionToken:  undefined,
            region: 'us-east-1',
            endpoint: undefined
        }
        var kinesisVideo = new AWS.KinesisVideo(options);
        var kinesisVideoArchivedContent = new AWS.KinesisVideoArchivedMedia(options);
        kinesisVideo.getDataEndpoint({
            StreamName: streamName,
            APIName: "GET_HLS_STREAMING_SESSION_URL"
        }, function(err, response) {
            if (err) { return console.error(err); }
            console.log('Data endpoint: ' + response.DataEndpoint);
            kinesisVideoArchivedContent.endpoint = new AWS.Endpoint(response.DataEndpoint);

            // Step 3: Get a Streaming Session URL
            var consoleInfo = 'Fetching ' + protocol + ' Streaming Session URL';
            console.log(consoleInfo);

                kinesisVideoArchivedContent.getHLSStreamingSessionURL({
                    StreamName: streamName,
                    PlaybackMode: playbackMode,
                    HLSFragmentSelector: {
                        FragmentSelectorType: 'SERVER_TIMESTAMP',
                        TimestampRange: playbackMode === "LIVE" ? undefined : {
                            StartTimestamp: new Date('2020-04-20T04:23:41.311Z'),
                            EndTimestamp: new Date('2020-04-20T05:23:41.311Z')
                            
                        }
                    },
                    ContainerFormat: $('#containerFormat').val(),
                    DiscontinuityMode: $('#discontinuityMode').val(),
                    DisplayFragmentTimestamp: $('#displayFragmentTimestamp').val(),
                    MaxMediaPlaylistFragmentResults: parseInt($('#maxResults').val()),
                    Expires: parseInt($('#expires').val())
                }, function(err, response) {
                    if (err) { return console.error(err); }
                    console.log('HLS Streaming Session URL: ' + response.HLSStreamingSessionURL);

                    // Step 4: Give the URL to the video player.
               
                        var playerElement = $('#hlsjs');
                        playerElement.show();
                        var player = new Hls();
                        console.log('Created HLS.js Player');
                        player.loadSource(response.HLSStreamingSessionURL);
                        player.attachMedia(playerElement[0]);
                        console.log('Set player source');
                        player.on(Hls.Events.MANIFEST_PARSED, function() {
                            $('#hlsjs').play();
                            console.log('Starting playback');
                        });
                    
                });
        
        });

            return (
           <div>
                 <LeftDrawer></LeftDrawer>
                 <div style={videoStyles.videoDisplayContainer}  >
                           <video style={videoStyles.videoDisplay} id="hlsjs"  controls autoPlay></video>

                      </div> 
                 <div  style={videoStyles.videoContainer}  >

                 
                   <div style = {videoStyles.videoInfo}> {/*  <DateTimePicker
          onChange={this.onChange}
          value={this.state.date}
        /> */}<h5> Enter Start Date <DatePicker selected = {this.state.startDate} onChange = {this.handleChange} style ={'border : 1px'}/> Enter Start Time <TimePicker/></h5>
        <h5> Enter End Date <DatePicker selected = {this.state.startDate} onChange = {this.handleChange}/> Enter End Time <TimePicker/></h5>

        {/*  <form><h5>Start TimeStamp <input id="start"></input>End TimeStamp <input id="end"></input></h5><Button>Go</Button></form> */}
              
               </div>
               </div>
          
                
            </div> 

            );
    
    }
}


const mapStateToProps = state =>{
   
    return {
        link : state.surveillanceReducer.link,
        allThreats : state.threatsReducer.allThreats,
     }
}

const mapDispatchStateToProps = dispatch => {
    
    return {
        surveillance : () => {
            axios.get(backendURL + '/live-surveillance').then(response=>{
                dispatch({type: "surveillance",payload : response.data})
            })
                    
            
        },
        threats : () => {
          /*  axios.get(backendURL + '/all-threats').then(response=>{
                console.log(response.data);
              // const threatIds = response.data;
               axios.post(backendURL + '/threat-details', response.data).then(response=>{
                      //   console.log(result);
                         dispatch({type: "threats",payload : response.data})
                })

                
            }) */
            axios.get(backendURL + '/all-threats').then(response=>{
          
                var threatsData = [];
                //dispatch({type: "threats",payload : response.data})
                for(var i = 0; i<response.data.length; i++)
                {
                    axios.post(backendURL + '/threat-details', response.data[i]).then(result=>{
                        threatsData.push(result.data);
                
                    }).then(function()  {
                        
                    if(threatsData.length == response.data.length)    
                        dispatch({type: "threats",payload : threatsData})
                    })
                }
               
            }) 

        },
    }
}

export default (connect(mapStateToProps,mapDispatchStateToProps)(SurveillanceHistory));
