    {
        this.setState({endTime : time});
    }
    convertMonth = month =>
    {
        switch(month)
        {
            case 'Jan' : return 1;
            case 'Feb' : return 2;
            case 'Mar' : return 3;
            case 'Apr' : return 4;
            case 'May' : return 5;
            case 'Jun' : return 6;
            case 'Jul' : return 7;
            case 'Aug' : return 8;
            case 'Sept' : return 9;
            case 'Oct' : return 10;
            case 'Nov' : return 11;
            case 'Dec' : return 12;
        }
    }
    handleSubmit = e => 
    {
        e.preventDefault();
        var startTime = this.state.startTime._d.toString().split(' ');
        var startDate = this.state.startDate.toString().split(' ');
        var endTime = this.state.endTime._d.toString().split(' ');
        var endDate = this.state.endDate.toString().split(' ');

        console.log(startTime);
        console.log(startDate);

        console.log(startTime[0]);
        console.log(startDate[1]);

        console.log(endTime[0]);
        console.log(endTime[1]);
      //  var startMonth = convertMonth(startDate[1]);
      //  var endMonth = convertMonth(endDate[1]);
      var startMonth, endMonth; 
      switch(startDate[1])
        {
            case 'Jan' : startMonth =  1;
            case 'Feb' : startMonth =  2;
            case 'Mar' : startMonth =  3;
            case 'Apr' : startMonth =  4;
            case 'May' : startMonth =  5;
            case 'Jun' : startMonth =  6;
            case 'Jul' : startMonth =  7;
            case 'Aug' : startMonth =  8;
            case 'Sept' : startMonth =  9;
            case 'Oct' : startMonth =  10;
            case 'Nov' : startMonth =  11;
            case 'Dec' : startMonth =  12;
        }

        switch(endDate[1])
        {
            case 'Jan' : endMonth =  1;
            case 'Feb' : endMonth =  2;
            case 'Mar' : endMonth =  3;
            case 'Apr' : endMonth =  4;
            case 'May' : endMonth =  5;
            case 'Jun' : endMonth =  6;
            case 'Jul' : endMonth =  7;
            case 'Aug' : endMonth =  8;
            case 'Sept' : endMonth =  9;
