import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import '../../App.css';
//import axios from 'axios';
//import {Redirect} from 'react-router';
import Button from '@material-ui/core/Button'
import {signupForm} from "../../styles/signupForm";
import {container} from "../../styles/container";
import {inputField} from "../../styles/inputField";

class SignUp extends Component {

    constructor(props){
        super(props);       
        this.onSubmit = this.onSubmit.bind(this);
    }
 
    

  
 
   componentDidMount()
   {
    
   }
    onSubmit = () => {
    
    }

    render() {
             
        return (
       
            <div style={container} >
                 <form  style={signupForm}onSubmit={console.log('SignUp')}>
                        <h3>SignUp</h3>
                       
                                <input 
                                    style={inputField}
                                    placeholder="First Name"
                                    name="firstname"
                                    type = "text"
                                />
                                
                               
                               <input
                                    style={inputField}
                                    placeholder="Last Name"
                                    name="lastname"
                                    type="text"
                                    component={this.renderField}
                                />
                               
                                <input
                                    style={inputField}
                                    placeholder="Username"
                                    name="username"
                                    type = "text"
                                    
                                   
                                />
                               
                                <input
                                     style={inputField}
                                    placeholder="Password"
                                    name="password"
                                    type = "password"
                                    component={this.renderField}
                                />
                                 <Field
                                    placeholder="Confirm Password"
                                    name="confirm"
                                    type = "password"
                                    component={this.renderField}
                                />
                                <Button  type="submit" fullWidth color="primary" variant="contained">Sign Up</Button>
                                
                            </form>

                        </div>

        );
    }
}



const mapStateToProps = state =>{
   
    return {
        authFlag : state.signupReducer.authFlag,
       // username : state.reducer.username,
      //  login_status : state.reducer.login_status,
       // type : state.reducer.type  
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        signup : (values) => {
                    dispatch({type: "signup",payload : values})
            
        }
    }
}
export default reduxForm({
   // validate,
    form: "SignupForm",
    reducer: "signupReducer"
})(connect(mapStateToProps,mapDispatchStateToProps)(SignUp));
