import React, { Component } from "react";
import '../../App.css';
import * as videoStyles from  "../../styles/video";
import axios from 'axios';

import { connect } from "react-redux";



class Timer extends Component {

    constructor(props){
        super(props); 
        //this.state = { time: new Date() }; 
    }
    componentDidMount(){
        this.interval = setInterval(() => {
            //this.setState({ time: new Date() });
            this.props.updateTime ();
        }, 1000); 
    }
    componentWillUnmount() { 
        clearInterval(this.interval);
    }

    render() {
        const { time } = this.state; 
{/* <div><h3 style={videoStyles.timer}>16:29:48</h3>
              
</div> */}
        return (
            <div><h3 style={videoStyles.timer}> {time.toLocaleTimeString()}</h3>
              
            </div>


   

        );
    }
}


const mapStateToProps = state =>{
   
    return {
        time : state.surveillanceReducer.time
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        updateTime : () => {
         
                dispatch({type: "updateTime"})
            
                    
            
        }
    }
}

export default (connect(mapStateToProps,mapDispatchStateToProps)(Timer));
