import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import '../../App.css';
//import axios from 'axios';
//import {Redirect} from 'react-router';
import Button from '@material-ui/core/Button'
import {signupForm} from "../../styles/signupForm";
import {container} from "../../styles/container";
import {authLeft} from "../../styles/authLeft"
import * as inputField from  "../../styles/inputField";
import {logo} from "../../styles/logo";
import {background} from "../../styles/drawer";

import {button} from "../../styles/button";
import { Link, Redirect } from "react-router-dom";

class SignIn extends Component {

    constructor(props){
        super(props);       
      //  this.onSubmit = this.onSubmit.bind(this);
      this.state ={
          redirectVar :null
      }
    }
 
    
    renderField(field) {
     
        return (
            <div>
                <input style= {field.style} required placeholder={field.placeholder} type={field.type} {...field.input} />
            </div>  
            
        );
    }

  
 
   componentDidMount()
   {
    
   }
    onSubmit = () => {
    
    }

    render() {
             
        return (
            
            <div >
                 <div>
                    <div style={logo}>REAL TIME VIDEO ANALYTICS</div>
                    <div style={logo}>FOR THREAT DETECTION</div>
                  </div>
                  <table>
                      <tr>
                          <td>
                              <div style={authLeft}>Testdjfksjfdeijflseinsgew</div>
                          </td>
                          <td>
            <div style={container} >
                {this.state.redirectVar}
                 <form  style={signupForm}onSubmit={console.log('SignUp')}>
                        <h2 style={{color: "rgb(0, 56, 83)", marginBottom : "30px", fontSize: "23px"}}>Sign In</h2>
                       
                                <input
                                    //style={inputField}
                                    placeholder="Username"
                                    name="username"
                                    style={inputField.longInput}
                                    type = "text"
                                  
                                   
                                />
                               
                                <input
                                     //style={inputField}
                                    placeholder="Password"
                                    name="password"
                                    style={inputField.longInput}
                                    type = "password"
                                 
                                />
                                
                                <Button  style={button} type="submit" fullWidth /* color="primary" */ variant="contained" onClick={()=> {this.setState({redirectVar : <Redirect to="/live-surveillance"/>})}}>Sign In</Button>
                                <Link to={'/'} >Forgot Password?</Link>
                            </form>

                        </div>
                        </td>
                        </tr>
                        </table>
</div>

        );
    }
}



const mapStateToProps = state =>{
   
    return {
        authFlag : state.signinReducer.authFlag,
        username : state.signinReducer.username,
        password : state.signinReducer.password
      //  login_status : state.reducer.login_status,
       // type : state.reducer.type  
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        signin : (values) => {
                    dispatch({type: "signin",payload : values})
            
        }
    }
}
export default reduxForm({
   // validate,
    form: "SigninForm",
    reducer: "signinReducer"
})(connect(mapStateToProps,mapDispatchStateToProps)(SignIn));
