import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import '../../App.css';
//import axios from 'axios';
//import {Redirect} from 'react-router';
import Button from '@material-ui/core/Button'
import {signupForm} from "../../styles/signupForm";
import {container} from "../../styles/container";
import * as inputField from  "../../styles/inputField";
import {button} from "../../styles/button";

class SignUp extends Component {

    constructor(props){
        super(props);       
      //  this.onSubmit = this.onSubmit.bind(this);
    }
 
    
    renderField(field) {
     
        return (
            <div>
                <input style= {field.style} required placeholder={field.placeholder} type={field.type} {...field.input} />
            </div>  
            
        );
    }

  
 
   componentDidMount()
   {
    
   }
    onSubmit = () => {
    
    }

    render() {
             
        return (
            <div>   Real-Time Video Analytics for Threat Detection
        
            <div style={container} >
                 <form  style={signupForm}onSubmit={console.log('SignUp')}>
                        <h3 style={{color: "rgba(240,163,10,1 )"}}>Sign Up</h3>
                       <table>
                        <tr><td>
                                <Field 
                                    placeholder="First Name"
                                    name="firstname"
                                    style= {inputField.shortLeftInput}
                                    type = "text"
                                    component={this.renderField}
                                /></td>                                
                              <td> 
                               <Field
                                   // style={inputField}
                                    placeholder="Last Name"
                                    name="lastname"
                                    style={inputField.shortRightInput}
                                    type="text"
                                    component={this.renderField}
                                />
                               </td>
                               </tr></table>
                                <Field
                                    //style={inputField}
                                    placeholder="Username"
                                    name="username"
                                    style={inputField.longInput}
                                    type = "text"
                                    component={this.renderField}
                                   
                                />
                               
                                <Field
                                     //style={inputField}
                                    placeholder="Password"
                                    name="password"
                                    style={inputField.longInput}
                                    type = "password"
                                    component={this.renderField}
                                />
                                 <Field
                                 // style={inputField}
                                    placeholder="Confirm Password"
                                    name="confirm"
                                    style={inputField.longInput}
                                    type = "password"
                                    component={this.renderField}
                                />
                                <Button  style={button} type="submit" fullWidth /* color="primary" */ variant="contained">Sign Up</Button>
                                
                            </form>

                        </div>
                </div>
        );
    }
}



const mapStateToProps = state =>{
   
    return {
        authFlag : state.signupReducer.authFlag,
        username : state.signupReducer.username,
        password : state.signupReducer.password
      //  login_status : state.reducer.login_status,
       // type : state.reducer.type  
     }
}

const mapDispatchStateToProps = dispatch => {
    return {
        signup : (values) => {
                    dispatch({type: "signup",payload : values})
            
        }
    }
}
export default reduxForm({
   // validate,
    form: "SignupForm",
    reducer: "signupReducer"
})(connect(mapStateToProps,mapDispatchStateToProps)(SignUp));
