const initialStore = {
    link : "",
    threatlinks : {}
}

const threatsReducer = (state = initialStore,action) => {
    
    if(action.type === "threatsummary"){
        console.log(action.payload);
        console.log(state.threatlinks[0]);
        console.log(state.threatlinks[action.payload]);
        return {
            ...state,
            link : state.threatlinks[action.payload],
        }

    } 
      
    if(action.type === "threats"){
        console.log(action.payload);
        return {
            ...state,
            threatlinks : action.payload,
        }
    } 
    return state;
}


export default threatsReducer;
